import { Router } from 'express';
import { PublishedInvitationController } from '../controller/index.js';
import { verifyAccessToken, verifyUser } from '../middleware/AuthMiddleware.js';
import { formAnswerValidation } from '../validation/GuestAnswerValidation.js';
import handleValidationErrors from '../middleware/handleValidationErrors.js';
import asyncHandler from "../middleware/asyncHandler.js";
const router = Router();
router.post('/guest-answers/:id', formAnswerValidation, handleValidationErrors, asyncHandler(PublishedInvitationController.submitGuestAnswers));
router.get('/guest-answers', verifyAccessToken, verifyUser, asyncHandler(PublishedInvitationController.getAllGuestAnswers));
router.get('/:id', asyncHandler(PublishedInvitationController.getInvitation));
router.get('/', verifyAccessToken, verifyUser, asyncHandler(PublishedInvitationController.getAllInvitations));
export default router;
